    public List<PersonList> GetEmployeeContactList(List<EmployeeItem> empList)
    {
      List<PersonList> empContactlist = new List<PersonList>();

      List<string> characters = new List<string>();

      for (char c = 'A'; c <= 'Z'; c++)
          characters.Add("" + c);

      foreach(string charGrp in characters){
      PersonList subList = new PersonList();
      subList.AddRange(empList.Where(x => x.LastName.Substring(0, 1) == charGrp));
      subList.Heading = charGrp;
      empContactlist.Add(subList);
      }

      return empContactlist;

    }



